  "generateModel" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>generateModel</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Generate ORM model</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Generate prisma model code for users with all fields from Schema.fields. Include field decorators<br/>for validation, uniqueness, defaults. Store in Output.modelCode.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L23:0-25:1"];
  "generateMigration" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>generateMigration</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Generate migration SQL</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Generate SQL migration code to create users table with all Schema.fields. Use proper PostgreSQL<br/>syntax with constraints. Store in Output.migrationCode.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L27:0-29:1"];
  "generateValidation" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>generateValidation</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Generate validation schema</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Generate Zod validation schema for users matching all Schema.fields. Include type checking, required<br/>validation, and format validation where applicable. Store in Output.validationCode.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L31:0-33:1"];
