  "generateModel" -> "generateMigration" [labelOverlay="75%", edgeURL="#L41:23-41:43"];
  "generateMigration" -> "generateValidation" [labelOverlay="75%", edgeURL="#L41:44-41:65"];
  "generateValidation" -> "verifyConsistency" [labelOverlay="75%", edgeURL="#L41:66-41:86"];
  "verifyConsistency" -> "end" [labelOverlay="75%", edgeURL="#L41:87-41:93"];

  // Inferred Dependencies
  "start" -> "Schema" [label="reads Schema.tableName", style=dashed, color=blue];
  "generateModel" -> "Output" [label="reads Output.orm", style=dashed, color=blue];
  "generateModel" -> "Schema" [label="reads Schema.tableName", style=dashed, color=blue];
  "generateMigration" -> "Schema" [label="reads Schema.tableName", style=dashed, color=blue];
  "generateValidation" -> "Schema" [label="reads Schema.tableName", style=dashed, color=blue];
}
