  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "APISpec" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>APISpec</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="baseUrl__key" align="left" balign="left">baseUrl</td><td port="baseUrl__value" align="left" balign="left">https://api.example.com</td></tr><tr><td port="endpoint__key" align="left" balign="left">endpoint</td><td port="endpoint__value" align="left" balign="left">/users</td></tr><tr><td port="method__key" align="left" balign="left">method</td><td port="method__value" align="left" balign="left">GET</td></tr><tr><td port="authType__key" align="left" balign="left">authType</td><td port="authType__value" align="left" balign="left">bearer</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L6:0-11:1"];
  "Generated" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Generated</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="code__key" align="left" balign="left">code</td><td port="code__value" align="left" balign="left"></td></tr><tr><td port="language__key" align="left" balign="left">language</td><td port="language__value" align="left" balign="left">javascript</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L13:0-16:1"];
