  "generateClient" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>generateClient</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Generate API client code</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Generate javascript code for an API client that calls GET https://api.example.com/users with bearer<br/>authentication. Store the generated code in Generated.code. Include error handling and proper<br/>async/await patterns.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L22:0-24:1"];
  "validateGenerated" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validateGenerated</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate generated code</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Review the code in Generated.code. Check for: 1) Proper error handling, 2) Correct HTTP method<br/>usage, 3) Authentication headers, 4) Async/await syntax. List any issues found.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L26:0-28:1"];
  "displayCode" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>displayCode</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Display generated code</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Display the final generated code from Generated.code with syntax highlighting markers</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L30:0-32:1"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b></td></tr><tr><td align="left">Code generation complete</td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000", URL="#L34:0-34:30"];

  // Edges
  "start" -> "generateClient" [labelOverlay="75%", edgeURL="#L36:6-36:23"];
  "generateClient" -> "validateGenerated" [labelOverlay="75%", edgeURL="#L36:24-36:44"];
  "validateGenerated" -> "displayCode" [labelOverlay="75%", edgeURL="#L36:45-36:59"];
  "displayCode" -> "end" [labelOverlay="75%", edgeURL="#L36:60-36:66"];

  // Inferred Dependencies
  "generateClient" -> "Generated" [label="reads Generated.language", style=dashed, color=blue];
  "generateClient" -> "APISpec" [label="reads APISpec.method", style=dashed, color=blue];
  "generateClient" -> "APISpec" [label="reads APISpec.baseUrl", style=dashed, color=blue];
  "generateClient" -> "APISpec" [label="reads APISpec.endpoint", style=dashed, color=blue];
  "generateClient" -> "APISpec" [label="reads APISpec.authType", style=dashed, color=blue];
}
