digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Context Operations</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr><tr><td port="maxSteps__key" align="left" balign="left">maxSteps</td><td port="maxSteps__value" align="left" balign="left">20</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "UserProfile" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>UserProfile</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="userId__key" align="left" balign="left">userId</td><td port="userId__value" align="left" balign="left"></td></tr><tr><td port="name__key" align="left" balign="left">name</td><td port="name__value" align="left" balign="left"></td></tr><tr><td port="email__key" align="left" balign="left">email</td><td port="email__value" align="left" balign="left"></td></tr><tr><td port="preferences__key" align="left" balign="left">preferences</td><td port="preferences__value" align="left" balign="left">{}</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L6:0-11:1"];
  "SessionData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>SessionData</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="loginTime__key" align="left" balign="left">loginTime</td><td port="loginTime__value" align="left" balign="left">0</td></tr><tr><td port="activityCount__key" align="left" balign="left">activityCount</td><td port="activityCount__value" align="left" balign="left">0</td></tr><tr><td port="lastAction__key" align="left" balign="left">lastAction</td><td port="lastAction__value" align="left" balign="left"></td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L13:0-17:1"];
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b></td></tr><tr><td align="left">Initialize user session</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Set the UserProfile.userId to 'user-12345', UserProfile.name to 'Alice Johnson', and<br/>UserProfile.email to 'alice@example.com'. Then set SessionData.loginTime to current timestamp.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L19:0-21:1"];
  "updateActivity" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>updateActivity</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Track user activity</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Increment SessionData.activityCount by 1 and set SessionData.lastAction to 'profile_view</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L23:0-25:1"];
  "displaySummary" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>displaySummary</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Show session summary</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Display all context values: UserProfile and SessionData</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L27:0-29:1"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b></td></tr><tr><td align="left">Session tracking complete</td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000", URL="#L31:0-31:31"];

  // Edges
  "start" -> "updateActivity" [labelOverlay="75%", edgeURL="#L33:6-33:23"];
  "updateActivity" -> "displaySummary" [labelOverlay="75%", edgeURL="#L33:24-33:41"];
  "displaySummary" -> "end" [labelOverlay="75%", edgeURL="#L33:42-33:48"];
}
