graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Tool Quality Improvement</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr><tr><td port="maxSteps__key" align="left" balign="left">maxSteps</td><td port="maxSteps__value" align="left" balign="left">30</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Evaluation" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Evaluation</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="toolName__key" align="left" balign="left">toolName</td><td port="toolName__value" align="left" balign="left"></td></tr><tr><td port="issues__key" align="left" balign="left">issues</td><td port="issues__value" align="left" balign="left">[]</td></tr><tr><td port="improvements__key" align="left" balign="left">improvements</td><td port="improvements__value" align="left" balign="left">[]</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L6:0-10:1"];
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b></td></tr><tr><td align="left">Identify target tool</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Use list_available_tools meta-tool with include_source=true. Pick a tool that could be improved<br/>(e.g., one with unclear description or missing error handling). Set Evaluation.toolName to its name.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L12:0-14:1"];
  "analyzeToolQuality" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyzeToolQuality</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze tool implementation</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Review the implementation of tool . Identify issues: unclear description, missing input validation,<br/>poor error handling, inefficient logic. Store issues in Evaluation.issues array.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L16:0-18:1"];
  "proposeImprovements" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>proposeImprovements</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Generate improvement proposal</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Based on Evaluation.issues, use propose_tool_improvement meta-tool to suggest concrete improvements<br/>for . Include specific code changes or description improvements.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L20:0-22:1"];
  "reviewProposal" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>reviewProposal</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Review improvement proposal</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Display the proposed improvements for  and explain their benefits.</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L24:0-26:1"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b></td></tr><tr><td align="left">Tool improvement proposed</td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000", URL="#L28:0-28:31"];

  // Edges
  "start" -> "analyzeToolQuality" [labelOverlay="75%", edgeURL="#L30:6-30:27"];
  "analyzeToolQuality" -> "proposeImprovements" [labelOverlay="75%", edgeURL="#L30:28-30:50"];
  "proposeImprovements" -> "reviewProposal" [labelOverlay="75%", edgeURL="#L30:51-30:68"];
  "reviewProposal" -> "end" [labelOverlay="75%", edgeURL="#L30:69-30:75"];

  // Inferred Dependencies
  "analyzeToolQuality" -> "Evaluation" [label="reads Evaluation.toolName", style=dashed, color=blue];
  "proposeImprovements" -> "Evaluation" [label="reads Evaluation.toolName", style=dashed, color=blue];
  "reviewProposal" -> "Evaluation" [label="reads Evaluation.toolName", style=dashed, color=blue];
}
