  "respondToAlert" -> "executeAction" [labelOverlay="75%", edgeURL="#L36:44-36:60"];
  "executeAction" -> "end" [labelOverlay="75%", edgeURL="#L36:61-36:67"];

  // Inferred Dependencies
  "start" -> "Alert" [label="reads Alert.severity", style=dashed, color=blue];
  "start" -> "Alert" [label="reads Alert.count", style=dashed, color=blue];
  "start" -> "Alert" [label="reads Alert.threshold", style=dashed, color=blue];
  "evaluateSeverity" -> "Alert" [label="reads Alert.count", style=dashed, color=blue];
  "evaluateSeverity" -> "Alert" [label="reads Alert.threshold", style=dashed, color=blue];
  "respondToAlert" -> "Alert" [label="reads Alert.severity", style=dashed, color=blue];
  "executeAction" -> "Response" [label="reads Response.action", style=dashed, color=blue];
  "executeAction" -> "Alert" [label="reads Alert.service", style=dashed, color=blue];
