graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Product Development System</b></font></td></tr><tr><td align="center"><font point-size="10">@Version(&quot;1.0&quot;)</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i> <i>@Singleton</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="budget__key" align="left" balign="left">budget : number</td><td port="budget__value" align="left" balign="left">50000</td></tr><tr><td port="timeline__key" align="left" balign="left">timeline : Duration</td><td port="timeline__value" align="left" balign="left">P3M</td></tr><tr><td port="model__key" align="left" balign="left">model : string</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr><tr><td port="reviewers__key" align="left" balign="left">reviewers : Array&lt;string&gt;</td><td port="reviewers__value" align="left" balign="left">[<br align="left"/>  &quot;product&quot;,<br align="left"/>  &quot;engineering&quot;,<br align="left"/>  &quot;design&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", peripheries=2];
  subgraph cluster_Discovery {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>Discovery</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "Discovery__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "idea" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>idea</b> <i>&lt;input&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="concept__key" align="left" balign="left">concept</td><td port="concept__value" align="left" balign="left">Mobile app for task management</td></tr><tr><td port="target__key" align="left" balign="left">target</td><td port="target__value" align="left" balign="left">productivity users</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
    "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i> <i>@Critical</i></td></tr><tr><td align="left">Analyze '{{ idea.concept }}' for {{<br/>idea.target }}. Budget: {{ config.budget<br/>}}, Timeline: {{ config.timeline }}.<br/>Recommend critical features.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">{{ config.model }}</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];
    "research" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>research</b> <i>&lt;task&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="sources__key" align="left" balign="left">sources</td><td port="sources__value" align="left" balign="left">[<br align="left"/>  &quot;market reports&quot;,<br align="left"/>  &quot;user surveys&quot;,<br align="left"/>  &quot;competitor analysis&quot;<br align="left"/>]</td></tr><tr><td port="input__key" align="left" balign="left">input</td><td port="input__value" align="left" balign="left">{{ analyze.output }}</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  subgraph cluster_Execution {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>Execution</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "Execution__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "design" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>design</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Create Designs</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="deliverables__key" align="left" balign="left">deliverables</td><td port="deliverables__value" align="left" balign="left">[<br align="left"/>  &quot;wireframes&quot;,<br align="left"/>  &quot;user flows&quot;,<br align="left"/>  &quot;design system&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "build" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>build</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Build Prototype</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="type__key" align="left" balign="left">type</td><td port="type__value" align="left" balign="left">technical spike</td></tr><tr><td port="technologies__key" align="left" balign="left">technologies</td><td port="technologies__value" align="left" balign="left">[<br align="left"/>  &quot;React Native&quot;,<br align="left"/>  &quot;TypeScript&quot;,<br align="left"/>  &quot;Supabase&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "test" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>test</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate Prototype</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="methods__key" align="left" balign="left">methods</td><td port="methods__value" align="left" balign="left">[<br align="left"/>  &quot;user testing&quot;,<br align="left"/>  &quot;technical review&quot;,<br align="left"/>  &quot;accessibility audit&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  "Review" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Review</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Stakeholder Review</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="reviewers__key" align="left" balign="left">reviewers</td><td port="reviewers__value" align="left" balign="left">{{ config.reviewers }}</td></tr><tr><td port="criteria__key" align="left" balign="left">criteria</td><td port="criteria__value" align="left" balign="left">[<br align="left"/>  &quot;user value&quot;,<br align="left"/>  &quot;technical feasibility&quot;,<br align="left"/>  &quot;market fit&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Approved" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Approved</b> <i>&lt;output&gt;</i></td></tr><tr><td align="left">Go to Production</td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "Rejected" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Rejected</b> <i>&lt;output&gt;</i></td></tr><tr><td align="left">Back to Discovery</td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Root-level notes
  "note_Discovery_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Discovery phase uses LLM reasoning to<br/>adaptively analyze concepts and<br/>recommend features based on constraints.</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_Discovery_0" -> "Discovery__cluster_anchor" [style=dashed, color="#999999", arrowhead=none];
  "note_Execution_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Execution phase combines design thinking<br/>with technical prototyping. Build task<br/>is async for parallel work.</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_Execution_0" -> "Execution__cluster_anchor" [style=dashed, color="#999999", arrowhead=none];
  "note_Review_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Review gates production deployment.<br/>Failures loop back to discovery with<br/>learnings, demonstrating iterative<br/>evolution.</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_Review_0" -> "Review" [style=dashed, color="#999999", arrowhead=none];

  // Edges
  "research" -> "design" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "test" -> "Review" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Review" -> "Approved" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>approved</b></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "Review" -> "Rejected" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>needs_work</b></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "Rejected" -> "analyze" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "idea" -> "analyze" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "research" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "design" -> "build" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "build" -> "test" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "analyze" -> "idea" [label="reads prompt", style=dashed, color=blue];
  "analyze" -> "idea" [label="reads prompt", style=dashed, color=blue];
  "analyze" -> "config" [label="reads prompt", style=dashed, color=blue];
  "analyze" -> "config" [label="reads prompt", style=dashed, color=blue];
  "analyze" -> "config" [label="reads model", style=dashed, color=blue];
  "research" -> "analyze" [label="reads input", style=dashed, color=blue];
  "Review" -> "config" [label="reads reviewers", style=dashed, color=blue];
}
