graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Consumer</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "LocalState" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LocalState</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Local node</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "PublicStateA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>PublicStateA</b></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "PublicTask" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>PublicTask</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];

  // Edges
  "LocalState" -> "PublicStateA" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "PublicStateA" -> "PublicTask" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
