  "RemoveItem" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>RemoveItem</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Remove Item</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "UpdateQuantity" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>UpdateQuantity</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Update Quantity</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ApplyCoupon" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ApplyCoupon</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Apply Coupon</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "CalculateTotal" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>CalculateTotal</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Calculate Total</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "AddItem" -> "ViewCart" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ViewCart" -> "RemoveItem" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "RemoveItem" -> "ViewCart" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ViewCart" -> "UpdateQuantity" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "UpdateQuantity" -> "CalculateTotal" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "CalculateTotal" -> "ViewCart" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ViewCart" -> "ApplyCoupon" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ApplyCoupon" -> "CalculateTotal" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
