graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Checkout Process</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "ShippingAddress" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ShippingAddress</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Shipping Address</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ShippingMethod" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ShippingMethod</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Shipping Method</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "PaymentInfo" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>PaymentInfo</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Payment Information</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ReviewOrder" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ReviewOrder</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Review Order</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ProcessPayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessPayment</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Process Payment</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "OrderConfirmation" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>OrderConfirmation</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order Confirmation</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "ShippingAddress" -> "ShippingMethod" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ShippingMethod" -> "PaymentInfo" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "PaymentInfo" -> "ReviewOrder" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ReviewOrder" -> "ProcessPayment" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessPayment" -> "OrderConfirmation" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>success</b></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessPayment" -> "PaymentInfo" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>failure</b></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
}
