graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Business Layer</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "ValidateInput" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ValidateInput</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Validate Input</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ApplyRules" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ApplyRules</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Apply Business Rules</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ExecuteLogic" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ExecuteLogic</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Execute Logic</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "QueryExecute" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>QueryExecute</b></td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "ResultTransform" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ResultTransform</b></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "ValidateInput" -> "ApplyRules" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ApplyRules" -> "ExecuteLogic" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ExecuteLogic" -> "QueryExecute" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "QueryExecute" -> "ResultTransform" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
