graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Data Layer</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "DatabaseConnect" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>DatabaseConnect</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Database Connection</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "QueryExecute" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>QueryExecute</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Execute Query</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ResultTransform" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ResultTransform</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Transform Result</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "CacheUpdate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>CacheUpdate</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Update Cache</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "DatabaseConnect" -> "QueryExecute" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "QueryExecute" -> "ResultTransform" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ResultTransform" -> "CacheUpdate" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
