graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Presentation Layer</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "RenderUI" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>RenderUI</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Render UI</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "HandleInput" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>HandleInput</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Handle User Input</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "DisplayResult" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>DisplayResult</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Display Result</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ValidateInput" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ValidateInput</b></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "ResultTransform" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ResultTransform</b></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "RenderUI" -> "HandleInput" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "HandleInput" -> "ValidateInput" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ValidateInput" -> "ResultTransform" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ResultTransform" -> "DisplayResult" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "DisplayResult" -> "RenderUI" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
