graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Data Ingestion</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "FetchExternal" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>FetchExternal</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Fetch External Data</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ValidateFormat" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ValidateFormat</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Validate Format</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "SanitizeData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>SanitizeData</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Sanitize Data</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "DetectSchema" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>DetectSchema</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Detect Schema</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "StoreRaw" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>StoreRaw</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Store Raw Data</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "FetchExternal" -> "ValidateFormat" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ValidateFormat" -> "SanitizeData" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "SanitizeData" -> "DetectSchema" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "DetectSchema" -> "StoreRaw" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
