digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Notifications</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "PrepareMessage" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>PrepareMessage</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Prepare Message</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "SendEmail" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>SendEmail</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Send Email</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "SendSlack" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>SendSlack</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Send Slack Message</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "LogNotification" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LogNotification</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Log Notification</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "UpdateDashboard" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>UpdateDashboard</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Update Dashboard</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "PrepareMessage" -> "SendEmail" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "SendEmail" -> "LogNotification" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "PrepareMessage" -> "SendSlack" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "SendSlack" -> "LogNotification" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "LogNotification" -> "UpdateDashboard" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
