graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Data Pipeline Orchestrator</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Initialize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Initialize</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Initialize Pipeline</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Monitor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Monitor</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Monitor Progress</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "HandleError" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>HandleError</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Handle Error</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Complete</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Pipeline Complete</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Archive" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Archive</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Archive Results</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "FetchExternal" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>FetchExternal</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "StoreRaw" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>StoreRaw</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "LoadRaw" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LoadRaw</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "StoreProcessed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>StoreProcessed</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "LoadProcessed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LoadProcessed</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "PublishResults" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>PublishResults</b></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "PrepareMessage" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>PrepareMessage</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "UpdateDashboard" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>UpdateDashboard</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];

  // Edges
  "Initialize" -> "FetchExternal" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "FetchExternal" -> "StoreRaw" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "StoreRaw" -> "LoadRaw" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "LoadRaw" -> "StoreProcessed" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "StoreProcessed" -> "LoadProcessed" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "LoadProcessed" -> "PublishResults" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "PublishResults" -> "PrepareMessage" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "PrepareMessage" -> "UpdateDashboard" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "UpdateDashboard" -> "Complete" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "Complete" -> "Archive" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "FetchExternal" -> "HandleError" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">error</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "LoadRaw" -> "HandleError" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">error</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "LoadProcessed" -> "HandleError" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">error</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "HandleError" -> "PrepareMessage" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
