graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Data Transformation</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "LoadRaw" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LoadRaw</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Load Raw Data</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ParseData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ParseData</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Parse Data</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "TransformStructure" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>TransformStructure</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Transform Structure</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "EnrichData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>EnrichData</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Enrich Data</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ValidateOutput" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ValidateOutput</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Validate Output</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "StoreProcessed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>StoreProcessed</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Store Processed Data</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "LoadRaw" -> "ParseData" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ParseData" -> "TransformStructure" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "TransformStructure" -> "EnrichData" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "EnrichData" -> "ValidateOutput" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ValidateOutput" -> "StoreProcessed" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
