graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Simplified App</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Start</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "End" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>End</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  subgraph cluster_Workflow {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>Workflow</b></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "Workflow__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "Validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Validate</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
    "Process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Process</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  }

  // Edges
  "Start" -> "Validate" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "Validate" -> "Process" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "Process" -> "End" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
