graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Common Patterns</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Authenticate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Authenticate</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">User Authentication</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Authorize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Authorize</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Authorization Check</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "SessionCreate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>SessionCreate</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Create Session</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ErrorHandler" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ErrorHandler</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Error Handler</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Retry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Retry</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Retry Logic</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Fallback" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Fallback</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Fallback State</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "Authenticate" -> "Authorize" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "Authorize" -> "SessionCreate" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ErrorHandler" -> "Retry" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "Retry" -> "Fallback" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
