graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Business Logic</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "ValidateOrder" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ValidateOrder</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Validate Order</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "CalculatePricing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>CalculatePricing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Calculate Pricing</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ApplyDiscounts" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ApplyDiscounts</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Apply Discounts</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ProcessPayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessPayment</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Process Payment</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "UpdateInventory" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>UpdateInventory</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Update Inventory</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "ValidateOrder" -> "CalculatePricing" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "CalculatePricing" -> "ApplyDiscounts" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ApplyDiscounts" -> "ProcessPayment" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessPayment" -> "UpdateInventory" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
