graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Integrated System</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "SystemStart" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>SystemStart</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">System Start</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "SystemReady" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>SystemReady</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">System Ready</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "LandingPage" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LandingPage</b></td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "APIGateway" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>APIGateway</b></td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "AuthService" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>AuthService</b></td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "DatabaseLayer" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>DatabaseLayer</b></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "ProductDisplay" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProductDisplay</b></td></tr></table>>, pad=0.5, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "ValidateOrder" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ValidateOrder</b></td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "ProcessPayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessPayment</b></td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "UpdateInventory" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>UpdateInventory</b></td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "UserDashboard" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>UserDashboard</b></td></tr></table>>, pad=0.5, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];

  // Edges
  "SystemStart" -> "LandingPage" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "LandingPage" -> "APIGateway" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "APIGateway" -> "AuthService" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "APIGateway" -> "DatabaseLayer" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProductDisplay" -> "ValidateOrder" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ValidateOrder" -> "ProcessPayment" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessPayment" -> "UpdateInventory" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "UpdateInventory" -> "DatabaseLayer" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "UserDashboard" -> "APIGateway" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "UpdateInventory" -> "SystemReady" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
