  "evaluating" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>evaluating</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Evaluating Quality</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
