  "evaluating" -> "needsRefinement" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">score &lt; 8 AND attempts &lt; max_attempts</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "needsRefinement" -> "refine" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "refine" -> "generate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "evaluating" -> "approved" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">attempts &gt;= max_attempts</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "approved" -> "finalContent" [labelOverlay="75%", labelhref="#srcLineTBD"];

