  "generate" -> "requirements" [label="reads prompt", style=dashed, color=blue];
  "generate" -> "requirements" [label="reads prompt", style=dashed, color=blue];
  "generate" -> "requirements" [label="reads prompt", style=dashed, color=blue];
  "refine" -> "evaluate" [label="reads prompt", style=dashed, color=blue];
