digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Customer Support Bot</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "agent" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>agent</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr><tr><td port="temperature__key" align="left" balign="left">temperature</td><td port="temperature__value" align="left" balign="left">0.7</td></tr><tr><td port="max_tokens__key" align="left" balign="left">max_tokens</td><td port="max_tokens__value" align="left" balign="left">1024</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "userMessage" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>userMessage</b> <i>&lt;input&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="text__key" align="left" balign="left">text</td><td port="text__value" align="left" balign="left"></td></tr><tr><td port="history__key" align="left" balign="left">history</td><td port="history__value" align="left" balign="left">[]</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "understand" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>understand</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Understand the user's intent: {{<br/>userMessage.text }}</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "classify" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>classify</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Classify as: question, complaint, or<br/>request</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "question" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>question</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Question Intent</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "complaint" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complaint</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Complaint Intent</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "request" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>request</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Request Intent</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "answerQuestion" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>answerQuestion</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Answer the question: {{ userMessage.text<br/>}}</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "handleComplaint" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>handleComplaint</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Address the complaint empathetically: {{<br/>userMessage.text }}</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "processRequest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processRequest</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process the request: {{ userMessage.text<br/>}}</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "response" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>response</b> <i>&lt;output&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="text__key" align="left" balign="left">text</td><td port="text__value" align="left" balign="left"></td></tr><tr><td port="action__key" align="left" balign="left">action</td><td port="action__value" align="left" balign="left"></td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "userMessage" -> "understand" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "understand" -> "classify" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "classify" -> "question" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "classify" -> "complaint" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "classify" -> "request" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "question" -> "answerQuestion" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "answerQuestion" -> "response" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "complaint" -> "handleComplaint" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "handleComplaint" -> "response" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "request" -> "processRequest" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "processRequest" -> "response" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "understand" -> "userMessage" [label="reads prompt", style=dashed, color=blue];
  "answerQuestion" -> "userMessage" [label="reads prompt", style=dashed, color=blue];
  "handleComplaint" -> "userMessage" [label="reads prompt", style=dashed, color=blue];
  "processRequest" -> "userMessage" [label="reads prompt", style=dashed, color=blue];
}
