graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Data Analyst</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr><tr><td port="tools__key" align="left" balign="left">tools</td><td port="tools__value" align="left" balign="left">[<br align="left"/>  &quot;calculator&quot;,<br align="left"/>  &quot;database_query&quot;,<br align="left"/>  &quot;chart_generator&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "dataRequest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>dataRequest</b> <i>&lt;input&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="query__key" align="left" balign="left">query</td><td port="query__value" align="left" balign="left">What were sales last quarter?</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "analyzeRequest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyzeRequest</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze: {{ dataRequest.query }}. Use<br/>available tools.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="tools__key" align="left" balign="left">tools</td><td port="tools__value" align="left" balign="left">#availableTools</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "executeTools" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>executeTools</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Execute required tool calls</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "synthesizeResults" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>synthesizeResults</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Synthesize results from tools: {{<br/>executeTools.results }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "report" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>report</b> <i>&lt;output&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="findings__key" align="left" balign="left">findings</td><td port="findings__value" align="left" balign="left"></td></tr><tr><td port="visualizations__key" align="left" balign="left">visualizations</td><td port="visualizations__value" align="left" balign="left">[]</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "dataRequest" -> "analyzeRequest" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyzeRequest" -> "executeTools" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "executeTools" -> "synthesizeResults" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "synthesizeResults" -> "report" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "analyzeRequest" -> "dataRequest" [label="reads prompt", style=dashed, color=blue];
  "synthesizeResults" -> "executeTools" [label="reads prompt", style=dashed, color=blue];
}
