graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Research Assistant</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "query" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>query</b> <i>&lt;input&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="question__key" align="left" balign="left">question</td><td port="question__value" align="left" balign="left"></td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "quickAnalysis" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>quickAnalysis</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Quick analysis: {{ query.question }}</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-haiku-20241022</td></tr><tr><td port="temperature__key" align="left" balign="left">temperature</td><td port="temperature__value" align="left" balign="left">0.3</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "simpleQuery" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>simpleQuery</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Simple Query</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "complexQuery" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complexQuery</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Complex Query</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "deepAnalysis" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>deepAnalysis</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Deep analysis: {{ query.question }}.<br/>Previous: {{ quickAnalysis.result }}</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr><tr><td port="temperature__key" align="left" balign="left">temperature</td><td port="temperature__value" align="left" balign="left">0.7</td></tr><tr><td port="max_tokens__key" align="left" balign="left">max_tokens</td><td port="max_tokens__value" align="left" balign="left">4096</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "synthesize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>synthesize</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Synthesize findings from: {{<br/>deepAnalysis.result }}</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr><tr><td port="temperature__key" align="left" balign="left">temperature</td><td port="temperature__value" align="left" balign="left">0.5</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "answer" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>answer</b> <i>&lt;output&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="summary__key" align="left" balign="left">summary</td><td port="summary__value" align="left" balign="left"></td></tr><tr><td port="detail__key" align="left" balign="left">detail</td><td port="detail__value" align="left" balign="left"></td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "query" -> "quickAnalysis" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "quickAnalysis" -> "simpleQuery" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>simple</b></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "simpleQuery" -> "answer" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "quickAnalysis" -> "complexQuery" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>complex</b></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "complexQuery" -> "deepAnalysis" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "deepAnalysis" -> "synthesize" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "synthesize" -> "answer" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "quickAnalysis" -> "query" [label="reads prompt", style=dashed, color=blue];
  "deepAnalysis" -> "query" [label="reads prompt", style=dashed, color=blue];
  "deepAnalysis" -> "quickAnalysis" [label="reads prompt", style=dashed, color=blue];
  "synthesize" -> "deepAnalysis" [label="reads prompt", style=dashed, color=blue];
}
