graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Text Analysis</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr><tr><td port="temperature__key" align="left" balign="left">temperature</td><td port="temperature__value" align="left" balign="left">0.7</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze the sentiment of the following<br/>text: {{ input.text }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "input" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>input</b> <i>&lt;input&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="text__key" align="left" balign="left">text</td><td port="text__value" align="left" balign="left">I love this product!</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "result" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>result</b> <i>&lt;output&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="sentiment__key" align="left" balign="left">sentiment</td><td port="sentiment__value" align="left" balign="left">TBD</td></tr><tr><td port="confidence__key" align="left" balign="left">confidence</td><td port="confidence__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "input" -> "analyze" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "result" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "analyze" -> "input" [label="reads prompt", style=dashed, color=blue];
}
