graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Conditional Evolution System</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="mode__key" align="left" balign="left">mode : string</td><td port="mode__value" align="left" balign="left">production</td></tr><tr><td port="maxRetries__key" align="left" balign="left">maxRetries : number</td><td port="maxRetries__value" align="left" balign="left">3</td></tr><tr><td port="enableLogging__key" align="left" balign="left">enableLogging : boolean</td><td port="enableLogging__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "performance" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>performance</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="avgResponseTime__key" align="left" balign="left">avgResponseTime : number</td><td port="avgResponseTime__value" align="left" balign="left">0</td></tr><tr><td port="peakMemory__key" align="left" balign="left">peakMemory : number</td><td port="peakMemory__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "adapter" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>adapter</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Read the config and performance<br/>contexts. Use get_machine_definition to<br/>see the current structure. Based on<br/>conditions, modify the machine:
    - If<br/>mode='production' and maxRetries &gt; 1:<br/>add retry logic with exponential<br/>backoff
    - If avgResponseTime &gt;<br/>1000ms: add caching layer
    - If<br/>enableLogging=true: add logging nodes at<br/>key points
    Use update_definition to<br/>apply changes.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "processing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Main processing logic</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">System operation complete</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "start" -> "adapter" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "adapter" -> "config" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "adapter" -> "performance" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "adapter" -> "processing" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "processing" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
