graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Rails-Based Meta-Programming</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "system" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>system</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="version__key" align="left" balign="left">version : string</td><td port="version__value" align="left" balign="left">1.0.0</td></tr><tr><td port="features__key" align="left" balign="left">features : string</td><td port="features__value" align="left" balign="left">basic</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "analyzer" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyzer</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze the system context using<br/>get_context_value. Get the current<br/>machine definition with<br/>get_machine_definition. If<br/>features='basic', evolve the machine to<br/>add advanced features: add<br/>'authentication' Task, 'authorization'<br/>Task, and 'audit' State nodes. Use<br/>update_definition with a clear reason<br/>for the modification.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "init" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>init</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">System initialization</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Execute main processing logic</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Execution complete</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "init" -> "analyzer" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyzer" -> "system" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyzer" -> "process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
