graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Tool Creation Example</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "sentiment_analyzer" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>sentiment_analyzer</b> <i>&lt;tool&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="description__key" align="left" balign="left">description</td><td port="description__value" align="left" balign="left">Analyze sentiment of text and<br align="left"/>return score</td></tr></table></td></tr></table>>, shape=component, fillcolor="#FFF9C4", style=filled, color="#F57F17"];
  "data_transformer" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>data_transformer</b> <i>&lt;tool&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="description__key" align="left" balign="left">description</td><td port="description__value" align="left" balign="left">Transform data from one format<br align="left"/>to another</td></tr><tr><td port="input_schema__key" align="left" balign="left">input_schema</td><td port="input_schema__value" align="left" balign="left">{<br align="left"/>  &quot;type&quot;: &quot;object&quot;,<br align="left"/>  &quot;properties&quot;: {<br align="left"/>    &quot;data&quot;: {<br align="left"/>      &quot;type&quot;: &quot;string&quot;<br align="left"/>    },<br align="left"/>    &quot;target_format&quot;: {<br align="left"/>      &quot;type&quot;: &quot;string&quot;<br align="left"/>    }<br align="left"/>  }<br align="left"/>}</td></tr></table></td></tr></table>>, shape=component, fillcolor="#FFF9C4", style=filled, color="#F57F17"];
  "builder" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>builder</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Inspect the tool nodes<br/>(sentiment_analyzer and<br/>data_transformer). For each tool, use<br/>construct_tool to register them with<br/>complete input_schema, output_schema,<br/>and an implementation strategy. For<br/>sentiment_analyzer, use 'agent_backed'<br/>strategy. For data_transformer, use<br/>'code_generation' strategy with sample<br/>JavaScript code.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "processor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processor</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Use the sentiment_analyzer tool to<br/>analyze: 'This is a great product!'.<br/>Then use data_transformer to convert<br/>some sample JSON to YAML format.</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Processing complete</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "builder" -> "sentiment_analyzer" [label="uses", labelOverlay="75%", labelhref="#srcLineTBD"];
  "builder" -> "data_transformer" [label="uses", labelOverlay="75%", labelhref="#srcLineTBD"];
  "builder" -> "processor" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "processor" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
