digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Self-Improving Data Pipeline</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "input" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>input</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="data__key" align="left" balign="left">data : array</td><td port="data__value" align="left" balign="left">[]</td></tr><tr><td port="format__key" align="left" balign="left">format : string</td><td port="format__value" align="left" balign="left">json</td></tr></table></td></tr><tr><td align="left" bgcolor="#FFCCCC"><font point-size="8"><b>ðŸ”´ 1 issue</b><br/>Type mismatch: expected array, got string<br/></font></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", penwidth=3, color="#D32F2F"];
  "output" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>output</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="processed__key" align="left" balign="left">processed : array</td><td port="processed__value" align="left" balign="left">[]</td></tr><tr><td port="metrics__key" align="left" balign="left">metrics : object</td><td port="metrics__value" align="left" balign="left">{}</td></tr></table></td></tr><tr><td align="left" bgcolor="#FFCCCC"><font point-size="8"><b>ðŸ”´ 2 issues</b><br/>Type mismatch: expected array, got string<br/>Type mismatch: expected object, got string<br/></font></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", penwidth=3, color="#D32F2F"];
  "toolRegistry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>toolRegistry</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="customTools__key" align="left" balign="left">customTools : array</td><td port="customTools__value" align="left" balign="left">[]</td></tr></table></td></tr><tr><td align="left" bgcolor="#FFCCCC"><font point-size="8"><b>ðŸ”´ 1 issue</b><br/>Type mismatch: expected array, got string<br/></font></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", penwidth=3, color="#D32F2F"];
  "idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>idle</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "processing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processing</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "optimizing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>optimizing</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ingest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ingest</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Load data from input context</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process the data. If no suitable tool<br/>exists, construct one.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "optimize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>optimize</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Review the tools used. Can they be<br/>improved?</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "idle" -> "ingest" [label="@auto @auto", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ingest" -> "input" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "ingest" -> "process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "input" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "output" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "toolRegistry" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "optimizing" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "optimizing" -> "toolRegistry" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "optimizing" -> "output" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "optimizing" -> "toolRegistry" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "optimizing" -> "complete" [label="when=metrics.quality > 0.9", labelOverlay="75%", labelhref="#srcLineTBD"];
  "optimizing" -> "process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "complete" -> "idle" [label="@auto @auto", labelOverlay="75%", labelhref="#srcLineTBD"];

  // Validation Warnings
  "warning_input_0" [label=<ðŸ”´ Type mismatch: expected array, got<br/>string>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_input_0" -> "input" [style=dashed, color="#999999", arrowhead=none];
  "warning_output_0" [label=<ðŸ”´ Type mismatch: expected array, got<br/>string>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_output_0" -> "output" [style=dashed, color="#999999", arrowhead=none];
  "warning_output_1" [label=<ðŸ”´ Type mismatch: expected object, got<br/>string>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_output_1" -> "output" [style=dashed, color="#999999", arrowhead=none];
  "warning_toolRegistry_0" [label=<ðŸ”´ Type mismatch: expected array, got<br/>string>, shape=note, fillcolor="#FFCCCC", style=filled, fontsize=9];
  "warning_toolRegistry_0" -> "toolRegistry" [style=dashed, color="#999999", arrowhead=none];

  // Inferred Dependencies
  "optimizing" -> "output" [label="condition references metrics", style=dashed, color=blue];
}
