digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Multi-Model Task System</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "quick_analysis" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>quick_analysis</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Provide a quick one-sentence summary of<br/>what this system does.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "deep_analysis" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>deep_analysis</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Perform an in-depth analysis of the<br/>implications of using different AI<br/>models for different tasks. Consider<br/>cost, latency, and quality trade-offs.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr><tr><td port="modelId__key" align="left" balign="left">modelId</td><td port="modelId__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "strategic_decision" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>strategic_decision</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Make a strategic recommendation on model<br/>selection strategy for a production<br/>system. Consider all factors including<br/>cost, performance, and reliability.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr><tr><td port="modelId__key" align="left" balign="left">modelId</td><td port="modelId__value" align="left" balign="left">claude-3-opus-20240229</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "analysis" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analysis</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="quickSummary__key" align="left" balign="left">quickSummary : string</td><td port="quickSummary__value" align="left" balign="left"></td></tr><tr><td port="deepAnalysis__key" align="left" balign="left">deepAnalysis : string</td><td port="deepAnalysis__value" align="left" balign="left"></td></tr><tr><td port="recommendation__key" align="left" balign="left">recommendation : string</td><td port="recommendation__value" align="left" balign="left"></td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Multi-model execution complete</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "start" -> "quick_analysis" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "quick_analysis" -> "analysis" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analysis" -> "deep_analysis" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "deep_analysis" -> "analysis" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analysis" -> "strategic_decision" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "strategic_decision" -> "analysis" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analysis" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
