graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Automated Transitions Demo</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "metrics" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>metrics</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="errorCount__key" align="left" balign="left">errorCount : number</td><td port="errorCount__value" align="left" balign="left">0</td></tr><tr><td port="processingTime__key" align="left" balign="left">processingTime : number</td><td port="processingTime__value" align="left" balign="left">0</td></tr><tr><td port="quality__key" align="left" balign="left">quality : number</td><td port="quality__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>idle</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "initializing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>initializing</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ready" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ready</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>error</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process the data and update metrics</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "handleError" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>handleError</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Diagnose and handle the error</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "idle" -> "initializing" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "initializing" -> "ready" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ready" -> "process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "metrics" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "metrics" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "handleError" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "handleError" -> "metrics" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "handleError" -> "metrics" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "handleError" -> "process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "handleError" -> "error" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "error" -> "idle" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "complete" -> "idle" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
