graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Dynamic Tool Construction Demo</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "input" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>input</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="text__key" align="left" balign="left">text : string</td><td port="text__value" align="left" balign="left">Sample text for analysis</td></tr><tr><td port="language__key" align="left" balign="left">language : string</td><td port="language__value" align="left" balign="left">en</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "output" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>output</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="results__key" align="left" balign="left">results : string</td><td port="results__value" align="left" balign="left">{}</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "toolRegistry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>toolRegistry</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="customTools__key" align="left" balign="left">customTools : string</td><td port="customTools__value" align="left" balign="left">[]</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>idle</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze the input text. If no analysis<br/>tool exists, construct one using<br/>construct_tool.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "idle" -> "analyze" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "input" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "output" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "toolRegistry" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "complete" -> "idle" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
