graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Tool Review and Improvement Demo</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "input" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>input</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="numbers__key" align="left" balign="left">numbers : string</td><td port="numbers__value" align="left" balign="left">[<br align="left"/>  10,<br align="left"/>  20,<br align="left"/>  30,<br align="left"/>  40,<br align="left"/>  50<br align="left"/>]</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "output" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>output</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="average__key" align="left" balign="left">average : number</td><td port="average__value" align="left" balign="left">0</td></tr><tr><td port="median__key" align="left" balign="left">median : number</td><td port="median__value" align="left" balign="left">0</td></tr><tr><td port="stddev__key" align="left" balign="left">stddev : number</td><td port="stddev__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "toolRegistry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>toolRegistry</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="tools__key" align="left" balign="left">tools : string</td><td port="tools__value" align="left" balign="left">[]</td></tr><tr><td port="improvements__key" align="left" balign="left">improvements : string</td><td port="improvements__value" align="left" balign="left">[]</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>idle</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "reviewing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>reviewing</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "calculate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>calculate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Calculate statistics. If no tool exists,<br/>construct one.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "review" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>review</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Review the tools used in calculate. Are<br/>they efficient? Can they be improved?</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "apply" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apply</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Apply the proposed improvements to tools</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "idle" -> "calculate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "calculate" -> "input" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "calculate" -> "output" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "calculate" -> "toolRegistry" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "calculate" -> "reviewing" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "reviewing" -> "review" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "review" -> "toolRegistry" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "review" -> "output" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "review" -> "toolRegistry" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "review" -> "apply" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "review" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "apply" -> "toolRegistry" [label="reads", labelOverlay="75%", labelhref="#srcLineTBD"];
  "apply" -> "toolRegistry" [label="writes", labelOverlay="75%", labelhref="#srcLineTBD"];
  "apply" -> "calculate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "complete" -> "idle" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
