graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Checkpoint Demo</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">info</td></tr><tr><td port="maxCheckpoints__key" align="left" balign="left">maxCheckpoints</td><td port="maxCheckpoints__value" align="left" balign="left">10</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "State" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>State</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="step__key" align="left" balign="left">step</td><td port="step__value" align="left" balign="left">0</td></tr><tr><td port="data__key" align="left" balign="left">data</td><td port="data__value" align="left" balign="left">[]</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "Step1" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Step1</b> <i>&lt;state&gt;</i> <i>@checkpoint</i></td></tr><tr><td align="left">Step 1</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Step2" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Step2</b> <i>&lt;state&gt;</i> <i>@checkpoint</i></td></tr><tr><td align="left">Step 2</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Step3" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Step3</b> <i>&lt;state&gt;</i> <i>@checkpoint</i></td></tr><tr><td align="left">Step 3</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Step4" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Step4</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Step 4</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "Step1" -> "Step2" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Step2" -> "Step3" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Step3" -> "Step4" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
