graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Checkpoint Example</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">info</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Initialize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Initialize</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Initialize system</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ImportantState" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ImportantState</b> <i>&lt;state&gt;</i> <i>@checkpoint</i></td></tr><tr><td align="left">Critical point</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ProcessMore" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessMore</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Continue processing</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "Initialize" -> "ImportantState" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ImportantState" -> "ProcessMore" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
