graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Compensating Transactions</b></font></td></tr><tr><td align="center"><font point-size="10">@errorHandling(&quot;compensate&quot;)</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">info</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "ReserveInventory" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ReserveInventory</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Reserve inventory</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ChargePayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ChargePayment</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Charge customer</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ShipOrder" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ShipOrder</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Ship order</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ReleaseInventory" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ReleaseInventory</b> <i>&lt;state&gt;</i> <i>@compensate</i></td></tr><tr><td align="left">Release inventory</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "RefundPayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>RefundPayment</b> <i>&lt;state&gt;</i> <i>@compensate</i></td></tr><tr><td align="left">Refund payment</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "ReserveInventory" -> "ChargePayment" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ChargePayment" -> "ShipOrder" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
