graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Continue On Error</b></font></td></tr><tr><td align="center"><font point-size="10">@errorHandling(&quot;continue&quot;)</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Process1" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Process1</b> <i>&lt;init&gt;</i></td></tr><tr><td align="left">Process type A</td></tr></table>>, pad=0.5, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "Process2" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Process2</b> <i>&lt;init&gt;</i></td></tr><tr><td align="left">Process type B</td></tr></table>>, pad=0.5, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "TaskA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>TaskA</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Task A</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "TaskB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>TaskB</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Task B</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "Process1" -> "TaskA" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Process2" -> "TaskB" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
