graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Debug Logging</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Counter" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Counter</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="value__key" align="left" balign="left">value</td><td port="value__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "Increment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Increment</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Increment counter</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Check" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Check</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Check value</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Done" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Done</b> <i>&lt;state&gt;</i></td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "Increment" -> "Check" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Check" -> "Increment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>when: &quot;Counter.value &lt; 10&quot;;</b></td></tr><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when</td><td align="left" balign="left">Counter.value &lt; 10</td></tr></table></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD", when="Counter.value < 10", style=solid, color="#4CAF50", penwidth=2];
  "Check" -> "Done" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when</td><td align="left" balign="left">Counter.value &gt;= 10</td></tr></table></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD", when="Counter.value >= 10", style=solid, color="#4CAF50", penwidth=2];

  // Inferred Dependencies
  "Check" -> "Counter" [label="condition references Counter", style=dashed, color=blue];
  "Check" -> "Counter" [label="condition references Counter", style=dashed, color=blue];
}
