graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Parallel Pipeline</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr><tr><td port="maxSteps__key" align="left" balign="left">maxSteps</td><td port="maxSteps__value" align="left" balign="left">100</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "DataIngestion" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>DataIngestion</b> <i>&lt;init&gt;</i></td></tr><tr><td align="left">Ingest data from source</td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "HealthMonitor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>HealthMonitor</b> <i>&lt;init&gt;</i></td></tr><tr><td align="left">Monitor system health</td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "ProcessData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessData</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Process ingested data</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "StoreResults" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>StoreResults</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Store processed results</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "AlertOnIssue" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>AlertOnIssue</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Alert if health issue detected</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "DataIngestion" -> "ProcessData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "ProcessData" -> "StoreResults" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "HealthMonitor" -> "AlertOnIssue" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
