graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Retry Example</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "FetchAPI" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>FetchAPI</b> <i>&lt;state&gt;</i> <i>@retry</i></td></tr><tr><td align="left">Call external API</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "ProcessResponse" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>ProcessResponse</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Process API response</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "HandleError" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>HandleError</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Handle API error</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "FetchAPI" -> "ProcessResponse" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "FetchAPI" -> "HandleError" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>when: &quot;error&quot;;</b></td></tr><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when</td><td align="left" balign="left">error</td></tr></table></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD", when="error", style=solid, color="#4CAF50", penwidth=2];
}
