graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Timeout Example</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "LongRunningTask" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>LongRunningTask</b> <i>&lt;state&gt;</i> <i>@timeout("5000")</i></td></tr><tr><td align="left">Long operation</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Success" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Success</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Task completed</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Timeout" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Timeout</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Task timed out</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "LongRunningTask" -> "Success" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "LongRunningTask" -> "Timeout" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>when: &quot;timeout&quot;;</b></td></tr><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when</td><td align="left" balign="left">timeout</td></tr></table></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD", when="timeout", style=solid, color="#4CAF50", penwidth=2];
}
