graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate results</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "mergeResults" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>mergeResults</b> <i>&lt;join&gt;</i></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];

  // Edges
  "start" -> "analyze" [label="@parallel @parallel", labelOverlay="75%", labelhref="#srcLineTBD"];
  "start" -> "validate" [label="@parallel @parallel", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "mergeResults" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validate" -> "mergeResults" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
