graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Document Approval</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "draft" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>draft</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Draft</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "review" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>review</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Under Review</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "approved" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>approved</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Approved</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "rejected" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>rejected</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Rejected</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "published" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>published</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Published</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "submit" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>submit</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Submit for review</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "approveTask" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>approveTask</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Approve document</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "rejectTask" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>rejectTask</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Reject document</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "publish" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>publish</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Publish document</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "revise" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>revise</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Revise document</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "draft" -> "submit" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "submit" -> "review" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "review" -> "approveTask" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "approveTask" -> "approved" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "review" -> "rejectTask" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "rejectTask" -> "rejected" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "approved" -> "publish" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "publish" -> "published" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "rejected" -> "revise" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "revise" -> "draft" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
