graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Game Session</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "session" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>session</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="score__key" align="left" balign="left">score : number</td><td port="score__value" align="left" balign="left">0</td></tr><tr><td port="lives__key" align="left" balign="left">lives : number</td><td port="lives__value" align="left" balign="left">3</td></tr><tr><td port="level__key" align="left" balign="left">level : number</td><td port="level__value" align="left" balign="left">1</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L3:0-7:2"];
  "playing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>playing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Playing</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L9:0-9:24"];
  "paused" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>paused</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Paused</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L10:0-10:22"];
  "gameOver" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>gameOver</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Game Over</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L11:0-11:27"];
  "victory" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>victory</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Victory</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L12:0-12:24"];
  "updateScore" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>updateScore</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Update score</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L14:0-16:2"];
  "checkWinCondition" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>checkWinCondition</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check win</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L18:0-18:35"];
  "checkLoseCondition" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>checkLoseCondition</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check lose</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L19:0-19:37"];

  // Edges
  "playing" -> "updateScore" [labelOverlay="75%", edgeURL="#L21:8-21:22"];
  "updateScore" -> "checkWinCondition" [labelOverlay="75%", edgeURL="#L21:23-21:43"];
  "checkWinCondition" -> "victory" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>score &gt;= 1000</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L22:18-22:44"];
  "checkWinCondition" -> "checkLoseCondition" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>score &lt; 1000</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L23:18-23:54"];
  "checkLoseCondition" -> "gameOver" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>lives &lt;= 0</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L24:19-24:43"];
  "checkLoseCondition" -> "playing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>lives &gt; 0</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L25:19-25:41"];

  // Inferred Dependencies
  "updateScore" -> "session" [label="reads session.score", style=dashed, color=blue];
}
