graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Attribute Anchors</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="text__key" align="left" balign="left">text</td><td port="text__value" align="left" balign="left">uses</td></tr><tr><td port="sourceAttribute__key" align="left" balign="left">sourceAttribute</td><td port="sourceAttribute__value" align="left" balign="left">endpoint</td></tr><tr><td port="targetAttribute__key" align="left" balign="left">targetAttribute</td><td port="targetAttribute__value" align="left" balign="left">operation</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "api" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>api</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="endpoint__key" align="left" balign="left">endpoint</td><td port="endpoint__value" align="left" balign="left">https://api.example.com</td></tr><tr><td port="token__key" align="left" balign="left">token : string</td><td port="token__value" align="left" balign="left">null</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "call_api" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>call_api</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Invoke API</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="operation__key" align="left" balign="left">operation</td><td port="operation__value" align="left" balign="left">GET</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "api" -> "call_api" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
