graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Complete Styling Demo</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  rankdir="LR";
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Process data</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", color="blue", shape="box", URL="#L11:0-17:1"];
  "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i> <i>@critical</i></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Validate</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", fillcolor="#ffcccc", penwidth="3", color="#cc0000", URL="#L20:0-22:1"];

  // Edges
  "process" -> "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"></table>>, labelOverlay="75%", edgeURL="#L25:8-25:54", color="green", penwidth="2"];
}
